function toNumber(value, options = {}) {
  const { method, radix, nanToZero } = options;
  let _method = "parseFloat";
  if (method === "int")
    _method = "parseInt";
  let resolved = typeof value === "number" ? value : Number[_method](value, radix);
  if (nanToZero && isNaN(resolved))
    resolved = 0;
  return resolved;
}

